import { useReducer } from "react";

// the goal  of this component is simply  to manage  the current context to data and provide that context  to all components that want access to it

import CartContext from "./cart-context";

const defaultCartState = {
  items: [],
  totalAmount: 0,
};
// in the reducer function , you can run different pieces of code depending on which action type was dispatched 
const cartReducer = (state, action) => {
  //return a new state snaphot
  return defaultCartState;
};

const CartProvider = (props) => {
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );

  const addItemToCartHandler = (item) => {
      dispatchCartAction({type:'ADD', item :item})
  };
  const removeItemFromCartHandler = (id) => {};

  //this will be the concrete context value thought  which will aso be updated over time
  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemFromCartHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {/*THis allows us to wrap any components that should get access to this context with this cart provider component */}
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
