import { useState } from "react";
import useInputs from "../Hooks/use-inputs";
const BasicForm = (props) => {

  useInputs()


  const [enteredName, setEnteredName] = useState("");
  const [enteredNameTouched ,setEnteredNameTouched] = useState(false)

  const enteredNameIsValid = enteredName.trim() !== ""
  const isInValidInputName = !enteredNameIsValid && enteredNameTouched
 console.log(isInValidInputName)

  // valitation on every keystroke
  const inputNameHandler = (e) => {
    setEnteredName(e.target.value);   
  };
  //valitaion on blur
  const onBlurHandler = (e) =>{
    console.log("blur")
    setEnteredNameTouched(true)
  } 

  //valitacion on submit form
  const onSubmitHandler = (e) => {
    e.preventDefault();

    setEnteredNameTouched(true)

    if (!enteredNameIsValid) {
      return;
    }

    setEnteredName("");
    setEnteredNameTouched(false)

  };

  const inputNameClasses = isInValidInputName ? "form-control invalid"
	: "form-control  ";

  return (
    <form onSubmit={onSubmitHandler}>
      <div className="control-group">
        <div className= {inputNameClasses}>
          <label htmlFor="name">First Name</label>
          <input
            onChange={inputNameHandler}
            onBlur={onBlurHandler}
            value={enteredName}
            type="text"
            id="name"
          />
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
